mapper.select:
( SELECT info.content_id, info.latitude, info.longitude, detail.theme_code FROM attraction_detail detail LEFT JOIN attraction_info AS info ON detail.content_id = info.content_id LEFT JOIN bookmark AS book ON book.member_id = #{memberId} AND detail.content_id = book.content_id WHERE info.title LIKE CONCAT('%', #{keyword}, '%')
AND info.sido_code = #{sidoCode}
AND detail.theme_code = #{themeCode}
AND detail.category_code = #{categoryCode}
AND info.content_id = #{contentId}
) UNION ALL ( SELECT k_info.content_id, k_info.latitude, k_info.longitude, k_detail.theme_code FROM k_attraction_detail k_detail LEFT JOIN k_attraction_info AS k_info ON k_detail.content_id = k_info.content_id LEFT JOIN bookmark AS book ON book.member_id = #{memberId} AND k_detail.content_id = book.content_id WHERE k_info.title LIKE CONCAT('%', #{keyword}, '%')
AND k_info.sido_code = #{sidoCode}
AND k_detail.theme_code = #{themeCode}
AND k_detail.category_code = #{categoryCode}
AND k_info.content_id = #{contentId}
)

mapper.insert:
INSERT INTO bookmark
(member_id, content_id)
SELECT #{memberId}, #{contentId}
WHERE NOT
EXISTS (
SELECT 1
FROM bookmark
WHERE member_id = #{memberId} AND
content_id = #{contentId}
);
